mapper.select:
SELECT * FROM freereply
WHERE bno = #{bno}
ORDER BY rno DESC
LIMIT #{paging.pageStart}, #{paging.cpp}

SELECT COUNT(*)
FROM freereply
WHERE bno = #{bno}

SELECT reply_pw 
FROM freereply
WHERE rno = #{rno}

mapper.update:
UPDATE freereply
SET reply = #{reply}, update_date = current_timestamp
WHERE rno = #{rno}
